INSERT INTO `art_works` (`email`, `issuer_id`, `artist`, `art_id`, `title`, `symbol`, `creationyear`, `dimensions`, `materials`, `description`, `document`, `display_location`, `artwork_value`, `tokens`, `tokens_for_sale`, `price_per_token`, `listing_starts`, `listing_ends`, `holding_period_ends`, `art_pix1`, `art_pix2`, `art_pix3`, `art_pix4`, `blockchainUrl`, `listing_status`, `requestdate`, `approvaldate`, `declinedate`, `id`) VALUES ('vocettt@outlook.com', '7', 'Samuel John', '33', 'His Dark Materials', 'HDM', 2019, 'Artwork Dimension', '&lt;p&gt;Although&amp;nbsp;&lt;em&gt;His Dark Materials&lt;/em&gt;&amp;nbsp;has been marketed as&amp;nbsp;&lt;a title=&quot;Young adult fiction&quot; href=&quot;https://en.wikipedia.org/wiki/Young_adult_fiction&quot;&gt;young adult fiction&lt;/a&gt;, and the central characters are children, Pullman wrote with no target audience in mind. The fantasy elements include&amp;nbsp;&lt;a title=&quot;Races and creatures in His Dark Materials&quot; href=&quot;https://en.wikipedia.org/wiki/Races_and_creatures_in_His_Dark_Materials#Witches&quot;&gt;witches&lt;/a&gt;&amp;nbsp;and&amp;nbsp;&lt;a title=&quot;Races and creatures in His Dark Materials&quot; href=&quot;https://en.wikipedia.org/wiki/Races_and_creatures_in_His_Dark_Materials#Armoured_bears_(panserbj%C3%B8rne)&quot;&gt;armoured polar bears&lt;/a&gt;; the trilogy also alludes to concepts from&amp;nbsp;&lt;a title=&quot;Physics&quot; href=&quot;https://en.wikipedia.org/wiki/Physics&quot;&gt;physics&lt;/a&gt;,&amp;nbsp;&lt;a title=&quot;Philosophy&quot; href=&quot;https://en.wikipedia.org/wiki/Philosophy&quot;&gt;philosophy&lt;/a&gt;, and&amp;nbsp;&lt;a title=&quot;Theology&quot; href=&quot;https://en.wikipedia.org/wiki/Theology&quot;&gt;theology&lt;/a&gt;. It functions in part as a retelling and inversion of&amp;nbsp;&lt;a title=&quot;John Milton&quot; href=&quot;https://en.wikipedia.org/wiki/John_Milton&quot;&gt;John Milton&lt;/a&gt;\\\'s epic&amp;nbsp;&lt;em&gt;&lt;a title=&quot;Paradise Lost&quot; href=&quot;https://en.wikipedia.org/wiki/Paradise_Lost&quot;&gt;Paradise Lost&lt;/a&gt;&lt;/em&gt;,&lt;sup id=&quot;cite_ref-butler2_2-0&quot; class=&quot;reference&quot;&gt;&lt;a href=&quot;https://en.wikipedia.org/wiki/His_Dark_Materials#cite_note-butler2-2&quot;&gt;[2]&lt;/a&gt;&lt;/sup&gt;&amp;nbsp;with Pullman commending humanity for what Milton saw as its most tragic failing,&amp;nbsp;&lt;a title=&quot;Original sin&quot; href=&quot;https://en.wikipedia.org/wiki/Original_sin&quot;&gt;original sin&lt;/a&gt;.&lt;sup id=&quot;cite_ref-3&quot; class=&quot;reference&quot;&gt;&lt;a href=&quot;https://en.wikipedia.org/wiki/His_Dark_Materials#cite_note-3&quot;&gt;[3]&lt;/a&gt;&lt;/sup&gt;&amp;nbsp;The trilogy has attracted controversy for its criticism of religion.&lt;/p&gt;', '&lt;p&gt;&lt;em&gt;&lt;strong&gt;His Dark Materials&lt;/strong&gt;&lt;/em&gt;&amp;nbsp;is a&amp;nbsp;&lt;a title=&quot;Trilogy&quot; href=&quot;https://en.wikipedia.org/wiki/Trilogy&quot;&gt;trilogy&lt;/a&gt;&amp;nbsp;of&amp;nbsp;&lt;a title=&quot;Fantasy literature&quot; href=&quot;https://en.wikipedia.org/wiki/Fantasy_literature&quot;&gt;fantasy novels&lt;/a&gt;&amp;nbsp;by&amp;nbsp;&lt;a title=&quot;Philip Pullman&quot; href=&quot;https://en.wikipedia.org/wiki/Philip_Pullman&quot;&gt;Philip Pullman&lt;/a&gt;&amp;nbsp;consisting of&amp;nbsp;&lt;em&gt;&lt;a title=&quot;Northern Lights (Pullman novel)&quot; href=&quot;https://en.wikipedia.org/wiki/Northern_Lights_(Pullman_novel)&quot;&gt;Northern Lights&lt;/a&gt;&lt;/em&gt;&amp;nbsp;(1995; published as&amp;nbsp;&lt;em&gt;The Golden Compass&lt;/em&gt;&amp;nbsp;in North America),&amp;nbsp;&lt;em&gt;&lt;a title=&quot;The Subtle Knife&quot; href=&quot;https://en.wikipedia.org/wiki/The_Subtle_Knife&quot;&gt;The Subtle Knife&lt;/a&gt;&lt;/em&gt;&amp;nbsp;(1997), and&amp;nbsp;&lt;em&gt;&lt;a title=&quot;The Amber Spyglass&quot; href=&quot;https://en.wikipedia.org/wiki/The_Amber_Spyglass&quot;&gt;The Amber Spyglass&lt;/a&gt;&lt;/em&gt;&amp;nbsp;(2000). It follows the&amp;nbsp;&lt;a title=&quot;Coming of age&quot; href=&quot;https://en.wikipedia.org/wiki/Coming_of_age&quot;&gt;coming of age&lt;/a&gt;&amp;nbsp;of two children,&amp;nbsp;&lt;a title=&quot;Lyra Belacqua&quot; href=&quot;https://en.wikipedia.org/wiki/Lyra_Belacqua&quot;&gt;Lyra Belacqua&lt;/a&gt;&amp;nbsp;and&amp;nbsp;&lt;a title=&quot;Will Parry (His Dark Materials)&quot; href=&quot;https://en.wikipedia.org/wiki/Will_Parry_(His_Dark_Materials)&quot;&gt;Will Parry&lt;/a&gt;, as they wander through a series of&amp;nbsp;&lt;a class=&quot;mw-redirect&quot; title=&quot;Parallel universe (fiction)&quot; href=&quot;https://en.wikipedia.org/wiki/Parallel_universe_(fiction)&quot;&gt;parallel universes&lt;/a&gt;. The novels have won a number of awards, including the&amp;nbsp;&lt;a title=&quot;Carnegie Medal (literary award)&quot; href=&quot;https://en.wikipedia.org/wiki/Carnegie_Medal_(literary_award)&quot;&gt;Carnegie Medal&lt;/a&gt;&amp;nbsp;in 1995 for&amp;nbsp;&lt;em&gt;Northern Lights&lt;/em&gt;&amp;nbsp;and the 2001&amp;nbsp;&lt;a title=&quot;Costa Book Awards&quot; href=&quot;https://en.wikipedia.org/wiki/Costa_Book_Awards&quot;&gt;Whitbread Book of the Year&lt;/a&gt;&amp;nbsp;for&amp;nbsp;&lt;em&gt;The Amber Spyglass&lt;/em&gt;. In 2003, the trilogy was ranked third on the&amp;nbsp;&lt;a title=&quot;BBC&quot; href=&quot;https://en.wikipedia.org/wiki/BBC&quot;&gt;BBC&lt;/a&gt;\\\'s&amp;nbsp;&lt;a title=&quot;The Big Read&quot; href=&quot;https://en.wikipedia.org/wiki/The_Big_Read&quot;&gt;The Big Read&lt;/a&gt;&amp;nbsp;poll.&lt;sup id=&quot;cite_ref-Big_Read_1-0&quot; class=&quot;reference&quot;&gt;&lt;a href=&quot;https://en.wikipedia.org/wiki/His_Dark_Materials#cite_note-Big_Read-1&quot;&gt;[1]&lt;/a&gt;&lt;/sup&gt;&lt;/p&gt;', '', 'Abuja', '100000.00', 100, 100, '100.00', '2023-01-01', '2023-01-31', '2023-03-31', '33_pix1.jpg', '33_pix2.jpg', '', '', NULL, 'Listed', '2023-01-12 09:19:33', '2023-01-12 10:24:24', NULL, '33');
