#
# TABLE STRUCTURE FOR: art_works
#

DROP TABLE IF EXISTS `art_works`;

CREATE TABLE `art_works` (
  `email` varchar(100) DEFAULT NULL,
  `issuer_id` bigint(20) DEFAULT NULL,
  `artist` varchar(100) DEFAULT NULL,
  `art_id` bigint(20) DEFAULT NULL,
  `title` varchar(255) DEFAULT NULL,
  `symbol` varchar(30) DEFAULT NULL,
  `creationyear` int(11) DEFAULT NULL,
  `dimensions` varchar(100) DEFAULT NULL,
  `materials` text DEFAULT NULL,
  `description` text DEFAULT NULL,
  `document` varchar(100) DEFAULT NULL,
  `display_location` varchar(255) DEFAULT NULL,
  `artwork_value` decimal(20,2) DEFAULT NULL,
  `tokens` int(11) DEFAULT NULL,
  `tokens_for_sale` int(11) DEFAULT NULL,
  `price_per_token` decimal(10,2) DEFAULT NULL,
  `listing_starts` date DEFAULT NULL,
  `listing_ends` date DEFAULT NULL,
  `holding_period_ends` date DEFAULT NULL,
  `art_pix1` varchar(100) DEFAULT NULL,
  `art_pix2` varchar(100) DEFAULT NULL,
  `art_pix3` varchar(100) DEFAULT NULL,
  `art_pix4` varchar(100) DEFAULT NULL,
  `blockchainUrl` varchar(255) DEFAULT NULL,
  `listing_status` varchar(30) DEFAULT NULL COMMENT 'Listed, Awaiting Approval, Not Approved',
  `requestdate` datetime DEFAULT NULL,
  `approvaldate` datetime DEFAULT NULL,
  `declinedate` datetime DEFAULT NULL,
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=34 DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;



#
# TABLE STRUCTURE FOR: brokers
#

DROP TABLE IF EXISTS `brokers`;

CREATE TABLE `brokers` (
  `company` varchar(100) DEFAULT NULL,
  `broker_id` varchar(30) DEFAULT NULL COMMENT 'CSCS Number',
  `address` varchar(255) DEFAULT NULL,
  `state` varchar(50) DEFAULT NULL,
  `email` varchar(100) DEFAULT NULL,
  `phone` varchar(15) DEFAULT NULL,
  `blockchain_address` varchar(255) DEFAULT NULL,
  `incorporationdate` date DEFAULT NULL,
  `accountstatus` tinyint(4) DEFAULT 0,
  `pwd` varchar(255) DEFAULT NULL,
  `account_name` varchar(100) DEFAULT NULL,
  `account_number` varchar(20) DEFAULT NULL,
  `bank_code` varchar(20) DEFAULT NULL,
  `bankname` varchar(100) DEFAULT NULL,
  `recipient_code` varchar(50) DEFAULT NULL,
  `bank_account_status` tinyint(4) DEFAULT NULL,
  `date_created` datetime DEFAULT NULL,
  `userId` int(11) DEFAULT NULL,
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=29 DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;



#
# TABLE STRUCTURE FOR: countries
#

DROP TABLE IF EXISTS `countries`;

CREATE TABLE `countries` (
  `country` varchar(80) NOT NULL,
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=251 DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;



#
# TABLE STRUCTURE FOR: daily_price
#

DROP TABLE IF EXISTS `daily_price`;

CREATE TABLE `daily_price` (
  `symbol` varchar(30) NOT NULL,
  `previous_close_price` decimal(10,2) DEFAULT NULL,
  `open_price` decimal(10,2) DEFAULT NULL,
  `high_price` decimal(10,2) DEFAULT NULL,
  `low_price` decimal(10,2) DEFAULT NULL,
  `close_price` decimal(10,2) DEFAULT NULL,
  `change` decimal(10,2) DEFAULT NULL COMMENT 'Day Close Price - Previous Day Closing Price',
  `change_percent` decimal(5,2) DEFAULT NULL,
  `trades` bigint(20) DEFAULT NULL COMMENT 'No of trades',
  `volume` bigint(20) DEFAULT NULL,
  `trade_value` decimal(20,2) DEFAULT NULL COMMENT 'Volume * Price',
  `price_date` date NOT NULL,
  `last_updated_date` datetime NOT NULL,
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  PRIMARY KEY (`id`),
  KEY `index_synbol_id` (`symbol`)
) ENGINE=InnoDB AUTO_INCREMENT=21 DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;



#
# TABLE STRUCTURE FOR: direct_orders
#

DROP TABLE IF EXISTS `direct_orders`;

CREATE TABLE `direct_orders` (
  `order_id` bigint(20) DEFAULT NULL,
  `broker_id` varchar(50) DEFAULT NULL,
  `investor_id` varchar(50) DEFAULT NULL,
  `transtype` varchar(4) DEFAULT NULL COMMENT 'Only Sell',
  `ordertype` varchar(20) DEFAULT NULL COMMENT 'Market or Limit',
  `symbol` varchar(30) DEFAULT NULL,
  `price` decimal(10,2) DEFAULT NULL,
  `qty` int(11) DEFAULT NULL,
  `available_qty` int(11) DEFAULT NULL,
  `orderstatus` varchar(20) DEFAULT NULL COMMENT 'Submitted, Not Active, Active, Cancelled, Executed',
  `broker_commission` decimal(20,2) DEFAULT NULL,
  `nse_commission` decimal(20,2) DEFAULT NULL,
  `transfer_fee` decimal(10,2) DEFAULT NULL,
  `sms_fee` decimal(10,2) DEFAULT NULL,
  `total_amount` decimal(20,2) DEFAULT NULL,
  `broker_recipient_code` varchar(50) DEFAULT NULL,
  `investor_recipient_code` varchar(50) DEFAULT NULL,
  `orderdate` datetime DEFAULT NULL,
  `last_update_date` datetime DEFAULT NULL,
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=195 DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;



#
# TABLE STRUCTURE FOR: historical_prices
#

DROP TABLE IF EXISTS `historical_prices`;

CREATE TABLE `historical_prices` (
  `symbol` varchar(30) NOT NULL,
  `previous_close_price` decimal(10,2) DEFAULT NULL,
  `open_price` decimal(10,2) DEFAULT NULL,
  `high_price` decimal(10,2) DEFAULT NULL,
  `low_price` decimal(10,2) DEFAULT NULL,
  `close_price` decimal(10,2) DEFAULT NULL,
  `change` decimal(10,2) DEFAULT NULL COMMENT 'Day Close Price - Previous Day Closing Price',
  `trades` int(11) DEFAULT NULL COMMENT 'No of trades',
  `volume` bigint(20) DEFAULT NULL,
  `trade_value` decimal(20,2) DEFAULT NULL COMMENT 'Volume * Price',
  `price_date` date NOT NULL,
  `last_updated_date` datetime DEFAULT NULL,
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  PRIMARY KEY (`id`),
  KEY `index_synbol_id` (`symbol`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;

