#
# TABLE STRUCTURE FOR: listed_artworks
#

DROP TABLE IF EXISTS `listed_artworks`;

CREATE TABLE `listed_artworks` (
  `uid` int(11) DEFAULT NULL,
  `symbol` varchar(30) DEFAULT NULL,
  `art_id` bigint(20) DEFAULT NULL,
  `tokens_on_issue` int(20) DEFAULT NULL,
  `tokens_for_sale` int(11) DEFAULT NULL,
