#
# TABLE STRUCTURE FOR: market_status
#

DROP TABLE IF EXISTS `market_status`;

CREATE TABLE `market_status` (
  `market_date` date DEFAULT NULL,
  `market_status` varchar(6) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;



#
# TABLE STRUCTURE FOR: messages
#

DROP TABLE IF EXISTS `messages`;

CREATE TABLE `messages` (
  `msgid` bigint(20) DEFAULT NULL,
  `header` varchar(255) DEFAULT NULL,
  `details` text DEFAULT NULL,
  `msgdate` datetime DEFAULT NULL,
  `category` enum('Message','News') DEFAULT NULL COMMENT 'News or Message',
  `expiredate` datetime DEFAULT NULL,
  `recipients` text DEFAULT NULL,
  `sender` varchar(100) DEFAULT NULL,
  `display_status` tinyint(4) DEFAULT 0,
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=206 DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;



#
# TABLE STRUCTURE FOR: orders
#

DROP TABLE IF EXISTS `orders`;

CREATE TABLE `orders` (
  `order_id` bigint(20) DEFAULT NULL,
  `broker_id` varchar(50) DEFAULT NULL,
  `investor_id` varchar(50) DEFAULT NULL,
  `transtype` varchar(4) DEFAULT NULL COMMENT 'Buy or Sell',
  `symbol` varchar(30) DEFAULT NULL,
  `price` decimal(10,2) DEFAULT NULL,
  `qty` int(11) DEFAULT NULL,
  `available_qty` int(11) DEFAULT NULL,
  `ordertype` varchar(50) DEFAULT NULL,
  `limit_market` varchar(6) DEFAULT NULL COMMENT 'Limit Or Market',
  `orderdate` datetime(3) DEFAULT NULL,
  `expirydate` datetime DEFAULT NULL,
  `orderstatus` varchar(20) DEFAULT NULL COMMENT 'Not Active, Active, Cancelled, Expired or Executed',
  `broker_commission` decimal(20,2) DEFAULT NULL,
  `nse_commission` decimal(20,2) DEFAULT NULL,
  `transfer_fee` decimal(10,2) DEFAULT NULL,
  `sms_fee` decimal(10,2) DEFAULT NULL,
  `broker_recipient_code` varchar(50) DEFAULT NULL,
  `investor_recipient_code` varchar(50) DEFAULT NULL,
  `total_amount` decimal(20,2) DEFAULT NULL COMMENT 'TradeAmount and Fees',
  `trade_amount` decimal(20,2) DEFAULT NULL,
  `canupdate` tinyint(4) DEFAULT 1,
  `date_updated` datetime(3) DEFAULT NULL,
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=152 DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;

#
# TABLE STRUCTURE FOR: ordertypes
#

DROP TABLE IF EXISTS `ordertypes`;

CREATE TABLE `ordertypes` (
  `ordertype` varchar(50) DEFAULT NULL,
  `description` varchar(255) DEFAULT NULL,
  `status` tinyint(4) DEFAULT 0,
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;



#
# TABLE STRUCTURE FOR: payment_log
#

DROP TABLE IF EXISTS `payment_log`;

CREATE TABLE `payment_log` (
  `payername` varchar(100) DEFAULT NULL,
  `email` varchar(50) DEFAULT NULL,
  `phone` varchar(20) DEFAULT NULL,
  `gateway` varchar(50) DEFAULT NULL,
  `transref` varchar(50) DEFAULT NULL,
  `channel` varchar(20) DEFAULT NULL,
