  `slug` varchar(100) DEFAULT NULL,
  `code` varchar(20) DEFAULT NULL,
  `longcode` varchar(40) DEFAULT NULL,
  `gateway` varchar(30) DEFAULT NULL,
  `active` tinyint(4) DEFAULT NULL,
  `is_deleted` tinyint(4) DEFAULT NULL,
  `createdAt` datetime DEFAULT NULL,
  `updatedAt` datetime DEFAULT NULL,
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=247 DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;



#
# TABLE STRUCTURE FOR: paystack_countries
#

DROP TABLE IF EXISTS `paystack_countries`;

CREATE TABLE `paystack_countries` (
  `name` varchar(50) DEFAULT NULL,
  `iso_code` varchar(5) DEFAULT NULL,
  `default_currency_code` varchar(5) DEFAULT NULL,
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;



#
# TABLE STRUCTURE FOR: paystack_settings
#

DROP TABLE IF EXISTS `paystack_settings`;

CREATE TABLE `paystack_settings` (
  `VerifyUrl` varchar(255) DEFAULT NULL,
